mapper.select:
select
count(1)
from t_pay_card
where brandid='yx'
and goods_id=#{goodsId}
and agent=#{agent}
and taskid=#{taskid}
and ctime>=#{startTime}
and ctime<=#{endTime}
and enable_flag=#{enableFlag}
and status=#{status}
and end_time>=NOW()
and end_time<NOW()
order by ctime desc

	limit #{offset},#{limit}

